import React, {Component, useEffect, useState} from "react";
import FirebaseDataService from "../../../../services/firebase.service";
// import {Table, Button} from 'reactstrap';
import {auth} from "../../../../services/firebase";
import Table from "components/Table/Table.js";
import Button from "@material-ui/core/Button";
import GridItem from "../../../../components/Grid/GridItem";
import Card from "../../../../components/Card/Card";
import CardHeader from "../../../../components/Card/CardHeader";
import CardBody from "../../../../components/Card/CardBody";
import GridContainer from "../../../../components/Grid/GridContainer";
import {makeStyles} from "@material-ui/core/styles";
import {Redirect, Switch} from "react-router-dom";

const styles = {
    cardCategoryWhite: {
        "&,& a,& a:hover,& a:focus": {
            color: "rgba(255,255,255,.62)",
            margin: "0",
            fontSize: "14px",
            marginTop: "0",
            marginBottom: "0"
        },
        "& a,& a:hover,& a:focus": {
            color: "#FFFFFF"
        }
    },
    cardTitleWhite: {
        color: "#FFFFFF",
        marginTop: "0px",
        minHeight: "auto",
        fontWeight: "300",
        fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
        marginBottom: "3px",
        textDecoration: "none",
        "& small": {
            color: "#777",
            fontSize: "65%",
            fontWeight: "400",
            lineHeight: "1"
        }
    }
};

const useStyles = makeStyles(styles);

function InternSurvey(props) {
    const classes = useStyles();
    const [surveyList, setSurveyList] = useState([])
    const [title, setTitle] = useState('')
    const [all, setAll] = useState('')
    const [submitted, setSubmitted] = useState(false)
    useEffect(() => {
        auth.onAuthStateChanged((user) => {
            if (user) {
                // User is signed in.
                // console.log(user);
            } else {
                // No user is signed in.
                // console.log("logged out");
                props.history.push("/login")
            }
        });
        FirebaseDataService.getSurveys('intern').onSnapshot(onDataChange);
        // unsubscribe();
    }, [])


    const onDataChange = (items) => {
        let surveys = [];

        items.forEach((item) => {
            // let id = item.id;
            let data = item.data();
            surveys.push(data);
        });

        console.log(surveys)
        setSurveyList(surveys)

    }

    const onChangeTitle = (e) => {
        setTitle(e.target.value)
    }

    const onChangeDescription = (e) => {
        setAll(e.target.value)
    }

    const groupArrayOfObjects = (list, key) => {
        return list.reduce(function (rv, x) {
            (rv[x[key]] = rv[x[key]] || []).push(x);
            return rv;
        }, {});
    }

    const saveCommodities = () => {
        let list = JSON.parse(all);
        let states = groupArrayOfObjects(list, 'state');
        let keys = Object.keys(states);
        keys.forEach(key => {
            pushState(key, {all: JSON.stringify(states[key])});
        });
    }

    const pushState = (state, data) => {
        FirebaseDataService.setCommodity(state, data)
            .then(() => {
                // console.log("Updated commodities successfully!");
                setSubmitted(true)
            })
            .catch((e) => {
                console.log(e);
            });
    }

    const deleteSurvey = (id) => {
        FirebaseDataService.deleteSurvey(id, 'intern')
            .then(() => {
                // console.log("Deleted survey successfully!");
            })
            .catch((e) => {
                console.log(e);
            });
    }

    const openSurvey = (e, id) => {
        // return <Redirect from="/admin" to={"/admin/internSurvey/" + id} />
        // e.preventDefault()
        props.history.push("internSurvey" + id)
    }

    const _getDateTime = (time) => {
        return time ? (new Date(time).toDateString() + " - " + new Date(time).toLocaleTimeString()) : 'NA'
    }

    const getTableValues = () => {
        let tableData = []

        tableData.push(surveyList.id,
            surveyList.farmer_first_name,
            surveyList.farmer_sir_name,
            surveyList.survey_state,
            surveyList.survey_district,
            surveyList.survey_tehsil,
            surveyList.survey_village,
            surveyList.farmer_mobile,
            surveyList.total_cultivated_land,
            surveyList.crops_sold_for_farm,
            surveyList.sale_type,
            _getDateTime(surveyList.time),
            surveyList.agentName,
            surveyList.agentId,
            surveyList.farmer_linked_to_fpo,
            surveyList.have_storage_facility,
            surveyList.storage_space_available)
        // console.log(tableData)
        return tableData
    //         <td className="text-right"><Button onClick={(e) => {
    //         e.preventDefault()
    //         deleteSurvey(item.id)
    //     }} outline size="sm" color="danger">
    //         Delete</Button>
    //     <hr/>
    //     <Button onClick={(e) => {
    //         // e.preventDefault()
    //         openSurvey(e,item.id)
    //     }} outline size="sm" color="primary">
    //         View</Button>
    // </td>
    // </tr>;
    // }))
    }


    return (
        <GridContainer>
            <GridItem xs={12} sm={12} md={12}>
                <Card>
                    <CardHeader color="primary">
                        <h4 className={classes.cardTitleWhite}>Simple Table</h4>
                        <p className={classes.cardCategoryWhite}>
                            Here is a subtitle for this table
                        </p>
                    </CardHeader>
                    <CardBody>
                        {/*<Table className="align-items-center table-flush" hover responsive>*/}
                        {/*    <thead className="thead-light">*/}
                        {/*    <tr>*/}
                        {/*        <th>#</th>*/}
                        {/*        <th>Farmer's Name</th>*/}
                        {/*        <th>Address</th>*/}
                        {/*        <th>Mobile</th>*/}
                        {/*        <th>Total Cultivated Land (in acres)</th>*/}
                        {/*        <th>Crops Sold for Farm Earning (in percent)</th>*/}
                        {/*        <th>Type of Sale</th>*/}
                        {/*        <th>Time/Date</th>*/}
                        {/*        <th>Agent name - (ID)</th>*/}
                        {/*        <th>Is the farmer linked to any FPO?</th>*/}
                        {/*        <th>Do you have a storage facility?</th>*/}
                        {/*        <th>Actions</th>*/}
                        {/*    </tr>*/}
                        {/*    </thead>*/}
                        {/*    <tbody>*/}
                        {/*{surveyList.map((item, index) => {*/}
                        {/*    return <tr key={item.id}>*/}
                        {/*        <td>{index + 1}</td>*/}

                        {/*        <th scope="row">{item['farmer_first_name'] + " " + item['farmer_sir_name']}</th>*/}
                        {/*        <td>*/}
                        {/*            {item["survey_state"]} <br/>*/}
                        {/*            {item["survey_district"]}*/}
                        {/*            <br/>*/}
                        {/*            {item["survey_tehsil"]}*/}
                        {/*            <br/>*/}
                        {/*            {item["survey_village"]}*/}

                        {/*        </td>*/}
                        {/*        <td>{item['farmer_mobile']}</td>*/}
                        {/*        <td>{item['total_cultivated_land']}</td>*/}
                        {/*        /!* <td>{item['major_crops_sown_in_kharif']}</td> *!/*/}
                        {/*        <td>{item['crops_sold_for_farm']}</td>*/}
                        {/*        /!* <td>{item['farmer_nearest_apmc']}</td> *!/*/}
                        {/*        /!* <td>{item['farmer_apcm_distance']}</td> *!/*/}
                        {/*        <td>{item['sale_type']}</td>*/}
                        {/*        <td>{_getDateTime(item['time'])}</td>*/}
                        {/*        <td>{item['agentName']}<br/>({item['agentId']})</td>*/}
                        {/*        <td>{item['farmer_linked_to_fpo']}</td>*/}
                        {/*        <td>{item['have_storage_facility']} {(item['have_storage_facility'] === 'Yes') && (<>*/}
                        {/*            <br/> ({item['storage_space_available']})</>)}</td>*/}
                        {/*        <td className="text-right"><Button onClick={(e) => {*/}
                        {/*            e.preventDefault()*/}
                        {/*            deleteSurvey(item.id)*/}
                        {/*        }} outline size="sm" color="danger">*/}
                        {/*            Delete</Button>*/}
                        {/*            <hr/>*/}
                        {/*            <Button onClick={(e) => {*/}
                        {/*                // e.preventDefault()*/}
                        {/*                openSurvey(e, item.id)*/}
                        {/*            }} outline size="sm" color="primary">*/}
                        {/*                View</Button>*/}
                        {/*        </td>*/}
                        {/*    </tr>;*/}
                        {/*})}*/}

                        {/*    </tbody>*/}
                        {/*</Table>*/}
                        <Table
                            tableHeaderColor="primary"
                            tableHead={
                                [
                                    "#",
                                    "Farmer's Name",
                                    "Address",
                                    "Mobile",
                                    "Total Cultivated Land (in acres)",
                                    "Crops Sold for Farm Earning (in percent)",
                                    "Type of Sale",
                                    "Time/Date",
                                    "Agent name - (ID)",
                                    "Is the farmer linked to any FPO?",
                                    "Do you have a storage facility?",
                                    "Actions"
                                ]
                            }

                            // tableData={getTableValues()}
                        />
                    </CardBody>
                </Card>
            </GridItem>
        </GridContainer>

    )
        ;


}

export default InternSurvey;
